    if (!availableLineIds.length) return
    const hasSelectedLine =
      selectedLineId && availableLineIds.includes(selectedLineId)

    if (!hasSelectedLine) {
      const nextLineId =
        (paramLineId && availableLineIds.includes(paramLineId) ? paramLineId : null) ??
