  if (v == null) return false

  // ìˆ«ìí˜•
  if (typeof v === "number") return v === 1

  // ë¬¸ìì—´í˜•
  const s = String(v).trim().toLowerCase()
  if (s === "1" || s === "y" || s === "yes" || s === "true") return true
  if (s === "0" || s === "n" || s === "no" || s === "false") return false

  // ê·¸ ì™¸ëŠ” falsy ì·¨ê¸‰
  return false
}

