  normalizeNeedToSend,
  normalizeStatus,
  toHttpUrl,
} from "./normalizers"
import { computeMetroProgress } from "./processFlow"

const CellRenderers = {
  defect_url: ({ value }) => {
    const href = toHttpUrl(value)
    if (!href) return null
    return (
      <a
        href={href}
        target="_blank"
        rel="noopener noreferrer"
        className="inline-flex items-center justify-center text-primary transition-colors hover:text-primary/80"
