}

/* =========================
 * 2) ì»´í¬ë„ŒíŠ¸
 * ======================= */
export function NeedToSendCell({
  meta,
  recordId,
  baseValue, // ì„œë²„/í…Œì´ë¸” ì›ë³¸ê°’ (true/false ë˜ëŠ” ê³¼ê±° 0/1 ë“±)
  disabled = false,
  disabledReason = "ì´ë¯¸ JIRA ì „ì†¡ë¨ (needtosend ìˆ˜ì • ë¶ˆê°€)",
