      {!isCollapsed && (
        <div className="flex flex-col gap-3 lg:flex-row lg:items-start">
          {statusSidebar ? (
            <div className="w-full flex-shrink-0 lg:max-w-[200px]">{statusSidebar}</div>
          ) : null}
          <div className="flex flex-1 flex-wrap items-start gap-2">
            {sectionBlocks}
          </div>
        </div>
      )}
    </fieldset>
  )
}
