 * 허용 형식:
 *  - YYYY-MM-DD
 *  - YYYY-MM-DD HH:mm
 *  - YYYY-MM-DDTHH:mm(초/타임존 포함 가능)
 */
function tryParseDate(value) {
  if (value instanceof Date) {
    return Number.isNaN(value.getTime()) ? null : value
  }
  if (typeof value === "string") {
    const s = value.trim()
