          <Button
            type="submit"
            className="md:self-end"
            disabled={isCreating || !lineId}
          >
            <IconPlus className="mr-1 size-4" />
            {LABELS.addButton}
          </Button>
        </form>

        {formError && (
          <p className="pt-2 text-xs text-destructive" role="alert">
            {formError}
          </p>
        )}
      </div>

      <div className="flex-1 min-h-0 rounded-lg border bg-background">
        <TableContainer className="max-h-full overflow-auto">
          <Table stickyHeader className="w-full table-fixed">
            {/* ✅ colgroup으로 컬럼 폭을 한 번에 관리 */}
            <colgroup>
              <col className="w-10" />  {/* ID */}
              <col className="w-24" />  {/* Main Step */}
              <col className="w-24" />  {/* Custom End Step */}
              <col className="w-32" />  {/* Actions (버튼이라 조금 넓게) */}
            </colgroup>

            <TableHeader>
              <TableRow>
                <TableHead className="text-center">ID</TableHead>
                <TableHead className="text-center">{LABELS.mainStep}</TableHead>
                <TableHead className="text-center">{LABELS.customEndStep}</TableHead>
                <TableHead className="text-right"></TableHead>
              </TableRow>
            </TableHeader>

            <TableBody>
              {isLoading && !hasLoadedOnce && (
                <TableRow>
                  <TableCell
                    colSpan={4}
                    className="h-24 text-center text-sm text-muted-foreground"
                  >
                    {LABELS.loading}
                  </TableCell>
                </TableRow>
              )}

              {!isLoading && entries.length === 0 && (
                <TableRow>
                  <TableCell
                    colSpan={4}
                    className="h-24 text-center text-sm text-muted-foreground"
                  >
                    {lineId ? LABELS.empty : "Select a line to view overrides."}
                  </TableCell>
                </TableRow>
              )}

              {entries.map((entry) => {
                const isEditing = editingId === entry.id
                const isSaving = Boolean(savingMap[entry.id])
                const rowError = rowErrors[entry.id]

                return (
                  <React.Fragment key={entry.id}>
                    <TableRow>
                      {/* ✅ ID 가운데 정렬 */}
                      <TableCell className="text-center font-mono text-xs text-muted-foreground">
                        {entry.id}
                      </TableCell>

                      {/* ✅ Main Step 가운데 정렬 */}
                      <TableCell className="text-center">
                        {isEditing ? (
                          <Input
                            value={editDraft.mainStep}
                            onChange={(event) =>
                              handleEditChange("mainStep", event.target.value)
                            }
                            maxLength={50}
                            disabled={isSaving}
                            className="text-center"
                          />
                        ) : (
                          <span className="font-medium">{entry.mainStep}</span>
                        )}
                      </TableCell>

                      {/* ✅ Custom End Step 가운데 정렬 */}
                      <TableCell className="text-center">
                        {isEditing ? (
                          <Input
                            value={editDraft.customEndStep ?? ""}
                            onChange={(event) =>
                              handleEditChange("customEndStep", event.target.value)
                            }
                            maxLength={50}
                            disabled={isSaving}
                            className="text-center"
                          />
                        ) : entry.customEndStep && entry.customEndStep.trim().length > 0 ? (
                          entry.customEndStep
                        ) : (
                          <span className="text-muted-foreground">-</span>
                        )}
                      </TableCell>

                      {/* ✅ Actions도 가운데 정렬 */}
                      <TableCell className="text-end">
                        <div className="inline-flex items-center justify-ends gap-2">
                          {isEditing ? (
                            <>
                              <Button
                                size="sm"
                                onClick={handleSave}
                                disabled={isSaving}
                                className="gap-1"
                              >
                                <IconDeviceFloppy className="size-4" />
                                Save
                              </Button>
                              <Button
                                size="sm"
                                variant="ghost"
                                onClick={cancelEditing}
                                disabled={isSaving}
                                className="gap-1"
                              >
                                <IconX className="size-4" />
                                Cancel
                              </Button>
                            </>
                          ) : (
                            <>
                              <Button
                                size="sm"
                                variant="ghost"
                                onClick={() => startEditing(entry)}
                                className="gap-1"
                              >
                                <IconPencil className="size-4" />
                                Edit
                              </Button>
                              <Button
                                size="sm"
                                variant="ghost"
                                onClick={() => handleDelete(entry)}
                                className="gap-1 text-destructive"
                              >
                                <IconTrash className="size-4" />
                                Delete
                              </Button>
                            </>
                          )}
                        </div>
                      </TableCell>
                    </TableRow>

                    {rowError && (
                      <TableRow>
                        <TableCell
                          colSpan={4}
                          className="bg-destructive/5 px-4 py-2 text-xs text-destructive text-center"
                        >
                          {rowError}
                        </TableCell>
                      </TableRow>
                    )}
                  </React.Fragment>
                )
              })}
            </TableBody>
          </Table>
        </TableContainer>
      </div>

    </section>
  )
}

