  const customEndStep =
    customRaw === null || customRaw === undefined
      ? ""
      : typeof customRaw === "string"
        ? customRaw
        : String(customRaw)

  return {
    id: String(rawId),
    lineId,
    mainStep: mainStepRaw,
    customEndStep,
  }
}

