]

const METRIC_OPTIONS = [
  { value: "rowCount", label: "진행 건수" },
  { value: "sendJiraCount", label: "Send Jira" },
]

const CATEGORY_COLORS = [
  "var(--chart-1)",
  "var(--chart-2)",
  "var(--chart-3)",
  "var(--chart-4)",
  "var(--chart-5)",
  "var(--primary)",
]

const LABELS = {
  titleSuffix: "Line E-SOP History",
