+  React.useEffect(() => {
+    setLocalRange(normalizeSelected(selected, isRange))
+  }, [selected, isRange])
+
+  function handleChange(part, value) {
+    const next = { ...localRange, [part]: value ? new Date(value) : undefined }
+    setLocalRange(next)
+    if (typeof onSelect === "function") {
+      if (isRange) {
